mapper.select:
		select f.*,m.nickName from follow f, member m
		where f.target = m.memberNum
    		and f.memberNum=#{memberNum}

		select f.*,m.nickName from follow f, member m
		where f.target = m.memberNum
			and f.memberNum=#{memberNum}
			and f.target in 
 				
					#{target.target}

		select f.*,m.nickName from follow f, member m
		where f.memberNum = m.memberNum
    	and f.target=#{target}

select * from follow
where memberNum = #{memberNum}
	and target = #{target}

mapper.delete:
delete from follow 
where memberNum = #{memberNum}
	and target = #{target}
